#!/bin/bash

# Alphintra Local GCP Simulation Environment Manager
# This script helps you start, stop, and manage the local development environment

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Function to print colored output
print_status() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

print_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

print_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

print_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

# Function to check if Docker is running
check_docker() {
    if ! docker info > /dev/null 2>&1; then
        print_error "Docker is not running. Please start Docker Desktop and try again."
        exit 1
    fi
    print_success "Docker is running"
}

# Function to start infrastructure services
start_infrastructure() {
    print_status "Starting infrastructure services..."
    docker-compose up -d postgres timescaledb redis zookeeper kafka mlflow minio prometheus grafana jaeger
    
    print_status "Waiting for services to be healthy..."
    sleep 10
    
    # Check service health
    print_status "Checking service health..."
    docker-compose ps
}

# Function to start application services
start_applications() {
    print_status "Starting application services..."
    docker-compose up -d gateway auth-service trading-api strategy-engine broker-simulator
    
    print_status "Waiting for applications to start..."
    sleep 15
    
    print_status "Application services status:"
    docker-compose ps
}

# Function to stop all services
stop_services() {
    print_status "Stopping all services..."
    docker-compose down
    print_success "All services stopped"
}

